div.script:
document.addEventListener('DOMContentLoaded', function() {
// Sidebar toggle functionality
const sidebar = document.getElementById('sidebar');
const sidebarToggle = document.getElementById('sidebarToggle');
const closeSidebar = document.getElementById('closeSidebar');
const overlay = document.getElementById('overlay');

function toggleSidebar() {
    sidebar.classList.toggle('show');
    overlay.classList.toggle('show');
    document.body.style.overflow = sidebar.classList.contains('show') ? 'hidden' : '';
}

if (sidebarToggle) {
    sidebarToggle.addEventListener('click', toggleSidebar);
}

if (closeSidebar) {
    closeSidebar.addEventListener('click', toggleSidebar);
}

if (overlay) {
    overlay.addEventListener('click', toggleSidebar);
}

// Add focus styles for keyboard navigation
document.addEventListener('keyup', function(e) {
    if (e.key === 'Tab') {
    document.documentElement.classList.add('keyboard-nav');
    }
});

// Smooth scrolling for anchor links
document.querySelectorAll('a[href^="#"]').forEach(anchor => {
    anchor.addEventListener('click', function(e) {
    e.preventDefault();
    const target = document.querySelector(this.getAttribute('href'));
    if (target) {
        target.scrollIntoView({
        behavior: 'smooth'
        });
    }
    });
});
});

document.addEventListener("DOMContentLoaded", function () {
    const checkboxes = document.querySelectorAll(".anaesthesia-checkbox");
    const reminderList = document.getElementById("reminder_list");
    const anaesthesiaInput = document.getElementById("anaesthesia_id");
    const anaesthesiaLength = document.getElementById("anaesthesia_length");
    const submitButton = document.getElementById("submit");
    const form = document.querySelector("form");
    checkboxes.forEach(function (checkbox) {
        checkbox.addEventListener("change", function () {
            // Uncheck other checkboxes
            checkboxes.forEach(cb => {
                if (cb !== this) cb.checked = false;
            });

            anaesthesiaInput.value = this.checked ? this.value : "";

            // Update reminder list
            reminderList.innerHTML = "";
            if (this.checked) {
                const reminders = this.getAttribute("data-reminders").split(",");
                reminders.forEach(reminder => {
                    if (reminder.trim() !== "") {
                        const li = document.createElement("li");
                        li.textContent = reminder.trim();
                        reminderList.appendChild(li);
                    }
                });
            }
        });
    });

    form.addEventListener("submit", function (event) {
        if (anaesthesiaLength.value != 1) {
            if (!anaesthesiaInput.value) {
                event.preventDefault();
                alert("Please select an anaesthesia option before submitting.");
            }
        }
    });
});
